package kdata.iftest;

public class IfTest2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a = 10;
		int b = 4;
		
		if(a>b){
			System.out.println("a가 더 큽니다.");
		}
		else if(a==b){
			System.out.println("a와 b가 같습니다.");
		}
		else{
			System.out.println("b가 더 큽니다.");
		}
		
	}

}
